resources.string:
uniform mat4 uMVPMatrix;

 attribute vec4 vPosition;
 attribute vec3 vColor;
 attribute vec2 vTextCoords;

 varying vec3 color;
 varying vec2 tCoords;

 void main() {
 color = vColor;
 tCoords = vTextCoords;
 gl_Position = uMVPMatrix * vPosition;
 }

precision mediump float;

 uniform sampler2D texture;
 varying vec3 color;
 varying vec2 tCoords;

 void main() {
 vec4 tex = texture2D(texture, tCoords);
 //gl_FragColor = mix(texture2D(texture, tCoords), vec4(color,tex.a), .1);
 gl_FragColor = mix(tex, vec4(color,tex.a), .1);
 }

uniform mat4 unMVPMatrix;

 attribute vec2 vPosition;
 attribute vec2 vTexCoords;

 varying vec2 fTexCoords;

 void main() {
 fTexCoords = vTexCoords;
 gl_Position = unMVPMatrix * vec4(vPosition, 0.0, 1.0);
 }

precision mediump float;

 uniform sampler2D unTexture;
 uniform vec3 unColor;
 uniform vec3 unSColor;
 uniform float unBlend; varying vec2 fTexCoords;

 void main() {
 vec4 tex = texture2D(unTexture, fTexCoords);
 gl_FragColor = tex * vec4(mix(unColor, unSColor, unBlend), 1.0);
 //gl_FragColor = mix(tex,vec4(0,0,0,tex.a), .1);
 }

uniform mat4 uMVPMatrix;

 attribute vec4 vPosition;
 attribute vec4 vColor;

 varying vec4 color;

 void main() {
 color = vec4(vColor.rgb, 1.0);
 gl_Position = uMVPMatrix * vPosition;
 }

precision mediump float;

 varying vec4 color;

 void main() {
 gl_FragColor = color;
 }
