resources.string:
uniform mat4 uMVPMatrix;
 attribute vec4 vPosition;
 attribute vec4 vColor;
 attribute vec2 vTextCoords;

 varying vec4 color;
 varying vec2 tCoords;

 void main() {
 color = vColor;
 tCoords = vTextCoords;
 gl_Position = uMVPMatrix * vPosition;
 }

precision mediump float;
 uniform sampler2D texture;
 varying vec4 color;
 varying vec2 tCoords;

 void main() {
 //gl_FragColor = color;
 gl_FragColor = texture2D(texture, tCoords);
 }

uniform mat4 uMVP;
 attribute vec4 vPosition;
 attribute vec4 vColor;
 varying vec4 color;

 void main() {
 color = vColor;
 gl_Position = uMVP * vPosition;
 }

precision mediump float;
 varying vec4 color;

 void main() {
 gl_FragColor = color;
 }
