resources.string:
uniform mat4 uMVPMatrix;

 attribute vec4 vPosition;
 attribute vec4 vColor;
 attribute vec2 vTextCoords;

 varying vec4 color;
 varying vec2 tCoords;

 void main() {
 color = vColor;
 tCoords = vTextCoords;
 gl_Position = uMVPMatrix * vPosition;
 }

precision mediump float;

 varying vec4 color;
 varying vec2 tCoords;

 void main() {
 gl_FragColor = color;
 }
